import React, {useState} from 'react';

const QuestionsBox = ({questions, answers}) =>{
    const [answer, setAnswer] = useState(answers);

    return (
        <div>
           <h4>{questions}</h4>
           <h4>{answer}</h4>
           {
               answers.map((answer, index)=>{
                   return(<button key={index} onClick={this.setAnswer()}>{answer}</button>)
               })
           }
        </div>
    )
}

export default QuestionsBox;
