import React, {useState} from 'react';

const QuestionsBox = ({questions, options, questionId}) =>{
    const [answer, setAnswer] = useState(options);

    return (
        <div>
           <h4>{questions}</h4>
           <h4>{answer}</h4>
           {
               options.map((option, index)=>{
                   return(<button key={`${questionId}_${index}`} onClick={()=>setAnswer({option})}>{option}</button>)
               })
           }
        </div>
    )
}

export default QuestionsBox;
