import React, { Component } from 'react';
import quizService from './api/quizService';
import QuestionBox from './components/QuestionBox';

class Quiz extends Component{
    state = {
        questionBank: []
    }
    getQuizeService(){
        quizService().then(response => {
            this.setState({
                questionBank: response
            })
        })
    }
    componentDidMount(){
        this.getQuizeService()
    }
    render(){
    
        return(
            <div>
                {
                    this.state.questionBank.map( ({question, answers, questionId}) => {
                        return(<QuestionBox question={question} options={answers}  keyquestionId={questionId} />)
                    })
                }
            </div>
        )
    }
}

export default Quiz;
