import React, {useState} from 'react';

const QuestionsBox = ({questions, options, correct}) =>{
    const [answer, setAnswer] = useState(options);

    return (
        <div>
           <h4>{questions}</h4>
           {
               answer.map((text, index)=>{
                   return(<button key={index} onClick={()=>setAnswer([text])}>{text}</button>)
               })   
           }
        </div>
    )
}

export default QuestionsBox;
