import React from 'react';

const QuestionBox = ({question, options}) =>{
    return(
    <div>
        <h2>{question}</h2>
        {
            options.map( (option) => {
                return(<button>{option}</button>)
            })
        }
    </div>
    )
}

export default QuestionBox;
