import React, {useState} from 'react';

const QuestionBox = ({question, options, saveAnswer}) =>{
    const [answer, setAnswer] = useState(options)

    return(
    <div>
        <h2>{question}</h2>
        {
            answer.map( (text, index) => {
                return(
                <button 
                key={index}
                onClick={()=>{
                    setAnswer([text])
                    saveAnswer(text)
                }}
                >   
                {text}
                </button>
                )
            })
        }
    </div>
    )
}

export default QuestionBox;
